"use strict";

var searchBtn = document.querySelector(".nav-search");
var closeSearchBar = document.querySelector(".closebtn-search");
var inputSearch = document.querySelector(".search-bar-input");
var inputSearchPage = document.querySelector(".search-bar--in");
var searchOverlay = document.querySelector(".search-overlay");
var closeBtnInput = document.querySelector(".close-btn-search--page");
var html = document.documentElement;

function openSearch() {
  searchOverlay.classList.add("search-overlay--active");
  document.documentElement.classList.add("hystmodal__opened");
}

function closeSearch() {
  searchOverlay.classList.remove("search-overlay--active");
  document.documentElement.classList.remove("hystmodal__opened");
}

searchBtn.addEventListener("click", openSearch);
closeSearchBar.addEventListener("click", closeSearch);

if (document.querySelector('.search-bar')) {
  inputSearchPage.addEventListener("keyup", function () {
    if (this.value != "") {
      document.querySelector(".close-btn-search--page").classList.add("close-btn-search--active");
      document.querySelector(".arrow-search--page").style.display = "none";
    } else {
      document.querySelector(".close-btn-search--page").classList.remove("close-btn-search--active");
      document.querySelector(".arrow-search--page").style.display = "inline-block";
    }
  });
  closeBtnInput.addEventListener("click", function () {
    inputSearchPage.value = "";
    closeBtnInput.classList.remove("close-btn-search--active");
    document.querySelector(".arrow-search--page").style.display = "inline-block";
  });
}
